import React, { useState } from 'react'
import styled from 'styled-components'
import HighlightOffIcon from '@mui/icons-material/HighlightOff';
import EditIcon from '@mui/icons-material/Edit';

function Todo({todos,completeTodo}) {

  const [edit,setEdit]=useState({
    id:null,
    value:""
  })

const style={
  textDecoration:"line-through",
  opacity: "0.4",
  width:"90%",
  padding: "16px",
  borderRadius: "5px",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  margin: "4px auto",
  marginTtop:"20px",
  height:"50px",
  background: "linear-gradient(90deg,rgba(255, 118, 20, 1) 0%,rgba(255, 84, 17, 1) 100%)",
}

const style2={
  width:"90%",
  padding: "16px",
  borderRadius: "5px",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  margin: "4px auto",
  marginTop:"20px",
  height:"50px",
  background: "linear-gradient(90deg,rgba(255, 118, 20, 1) 0%,rgba(255, 84, 17, 1) 100%)",

}

  return todos.map((todo,index)=>(
    <Container
    style={
      todo.isComplete? style:
    style2}
    key={index}
    >
        <div
        key={todo.id}
        onClick={()=>completeTodo(todo.id)}
          >
        </div>
        <IconContent>
        <HighlightOffIcon/>
        <EditIcon/>
        </IconContent>
    </Container>
  ))
}

export default Todo


const Container=styled.div`

width:90%;
  padding: 16px;
  border-radius: 5px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 4px auto;
  margin-top:20px;
  height:50px;
  background: linear-gradient(90deg,rgba(255, 118, 20, 1) 0%,rgba(255, 84, 17, 1) 100%);
div{
    border:none;
    flex:1;
    height:20px;
    background-color:transparent;
    font:inherit;
}

`


const IconContent=styled.div`
display:flex;
align-items:center;
padding:2px;
gap:5px;
`
